package encoding

import org.specs2.Specification
import org.specs2.mock.Mockito
import org.apache.commons.codec.binary.Base64
import scalaxb.Base64Binary
import scalax.io.Resource
import scalax.file.Path
import org.specs2.specification.Step

class Base64Spec extends Specification {
  val test_results_path = "./test_results/"

  def is =
    args(sequential = true) ^
      "This spec verifies '64 bit encoding' " ^
      p ^
      "'Scala-IO' should " ^
      "Encode - Decode with Apache" ! encodeDecodeWApache ^
      "Encode - Decode with Apache, storing into Base64Binary" ! encodeDecodeWApacheIntoScalaxbBase64BinaryContainer ^
      "Encode Apache decode with Scalaxb" ! encodeDecodeWApacheIntoScalaxbBase64BinaryContainer ^
      "Encode / Decode with Scalaxb" ! encodeDecodeScalaxb ^
      "Can Decode / Encode image saved to disk with Apache" ! encodeDecodeWithApacheAndSaveImage ^
      "Can Decode / Encode image saved to disk with ScalaXB .8 fails" ! encodeDecodeWithScalaXbAndSaveImage ^
      "CRLF - Can Decode / Encode image saved to disk with ScalaXB .8 fails" ! encodeDecodeWithScalaXbAndSaveImageCRLF ^
      "CRLF - Can Decode / Encode image saved to disk with Apache" ! encodeDecodeWithApacheAndSaveImageCRLF ^
      "Can Decode / Encode opening image from disk with Apache" ! encodeDecodeWithApacheAndOpenExistingImage ^
      "Can Decode / Encode opening image from disk with ScalaXB" ! encodeDecodeWithScalaXbAndOpenExistingImage ^
      Step()

  end

  def encodeDecodeWApache = {
    val orig = "original String before base64 encoding in Java"
    val encoded = Base64.encodeBase64(orig.getBytes())
    val decoded = Base64.decodeBase64(encoded)
    orig.toList == decoded.toList
  }

  def encodeDecodeWApacheIntoScalaxbBase64BinaryContainer = {
    val orig = "original String before base64 encoding in Java"
    val encoded = Base64.encodeBase64(orig.getBytes())
    val scalaxbBase64 = new Base64Binary(Vector(encoded: _*))
    orig == new String(Base64.decodeBase64(scalaxbBase64.toArray))
  }

  def encodeApacheDecodeScalaxb = {
    val orig = "original String before base64 encoding in Java"
    val encoded = Base64.encodeBase64String(orig.getBytes)
    val decoded = Base64Binary.apply(encoded)
    val decodedString: String = new java.lang.String(decoded.toIndexedSeq.toArray)
    orig == decodedString
  }

  def encodeDecodeScalaxb = {
    val orig = "original String before base64 encoding in Java"
    val setup = new Base64Binary(Vector(orig.getBytes: _*))
    val encoded = setup.toString
    val decoded = Base64Binary.apply(encoded)
    val decodedString: String = new java.lang.String(decoded.toIndexedSeq.toArray)
    orig == decodedString
  }

