  def encodeDecodeWithApacheAndSaveImage = {
    val path = Path.fromString(test_results_path + "test.jpg")
    val decoded2 = Base64.decodeBase64(encodedPic)
    path.write(decoded2)
    val file = Resource.fromFile(test_results_path + "test.jpg")
    val encoded = Base64.encodeBase64String(file.byteArray)
    path.delete()
    val test = encodedPic == encoded
    val diff = encodedPic.diff(encoded)
    test
  }

  def encodeDecodeWithApacheAndSaveImageCRLF = {
    val path = Path.fromString(test_results_path + "test.jpg")

    val decoded2 = Base64.decodeBase64(encodedPicWithCRLF)
    path.write(decoded2)
    val file = Resource.fromFile(test_results_path + "test.jpg")
    val encoded = Base64.encodeBase64String(file.byteArray)
    path.delete()
    val test = encodedPicWithCRLF.filterNot(_ == '\n').filterNot(_ == ' ') == encoded
    val diff = encodedPicWithCRLF.filterNot(_ == '\n').filterNot(_ == ' ').diff(encoded)
    test
  }

  def encodeDecodeWithApacheAndOpenExistingImage = {
    val file = Resource.fromFile("./resources/test/android-eat-apple.jpg")

    val encoded = Base64.encodeBase64String(file.byteArray)
    val decoded2 = Base64.decodeBase64(encoded)
    val path = Path.fromString(test_results_path + "testDroidApache.jpg")
    path.write(decoded2)

    val test = decoded2.toList == file.byteArray.toList
    path.delete()
    test
  }

  def encodeDecodeWithScalaXbAndOpenExistingImage = {
    val file = Resource.fromFile("./resources/test/android-eat-apple.jpg")

    val encoded = Base64Binary.apply(file.byteArray: _*).toString
    val decoded2 = Base64Binary.apply(encoded).toIndexedSeq.toArray
    val path = Path.fromString(test_results_path + "testDroidScalaXB.jpg")
    path.write(decoded2)

    val test = decoded2.toList == file.byteArray.toList
    path.delete()
    test
  }

  def encodeDecodeWithScalaXbAndSaveImage = {
    val path = Path.fromString(test_results_path + "testSaveScalaXB.jpg")
    val decoded = Base64Binary.apply(encodedPic)
    path.write(decoded.toIndexedSeq.toArray)
    val file = Resource.fromFile("testSaveCRLFScalaXB.jpg")
    val b64 = new Base64Binary(Vector(file.byteArray: _*))
    val encodedByScalaXB = b64.toString
    val test = encodedPic != encodedByScalaXB
